// src/components/Dashboard.js
import React from "react";
import Profile from "./Profile";
import Instructions from "./Instructions";
import AddBike from "./AddBike";
import BikeDetails from "./BikeDetails";
import ReserveAlert from "./ReserveAlert";
import PetrolPump from "./PetrolPump";
import Mileage from "./Mileage";
import Summary from "./Summary";

const Dashboard = ({ tab, setTab, onLogout, user }) => {
  const tabs = [
    { key: "profile", label: "üë§ Profile" },
    { key: "instructions", label: "üìò Instructions" },
    { key: "addBike", label: "‚ûï Add Bike" },
    { key: "bikeDetails", label: "üìã Bike Details" },
    { key: "reserveAlert", label: "üîî Reserve Alert" },
    { key: "petrolPump", label: "‚õΩ Petrol Pump" },
    { key: "mileage", label: "üìä Mileage" },
    { key: "summary", label: "üìà Summary" },
  ];

  const renderTabContent = () => {
    switch (tab) {
      case "profile":
        return <Profile user={user} />;
      case "instructions":
        return <Instructions />;
      case "addBike":
        return <AddBike />;
      case "bikeDetails":
        return <BikeDetails />;
      case "reserveAlert":
        return <ReserveAlert />;
      case "petrolPump":
        return <PetrolPump />;
      case "mileage":
        return <Mileage />;
      case "summary":
        return <Summary />;
      default:
        return null;
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "sans-serif" }}>
      {/* Left Sidebar Tabs */}
      <div
        style={{
          width: "220px",
          backgroundColor: "#f0f0f0",
          padding: "20px",
          borderRight: "1px solid #ccc",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>üèçÔ∏è Dashboard</h2>
        {tabs.map((t) => (
          <button
            key={t.key}
            onClick={() => setTab(t.key)}
            style={{
              display: "block",
              width: "100%",
              padding: "10px",
              marginBottom: "10px",
              textAlign: "left",
              backgroundColor: tab === t.key ? "#4CAF50" : "#fff",
              color: tab === t.key ? "#fff" : "#000",
              border: "1px solid #ccc",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            {t.label}
          </button>
        ))}
        <button
          onClick={onLogout}
          style={{
            marginTop: "40px",
            padding: "10px",
            backgroundColor: "#ff4d4d",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            width: "100%",
          }}
        >
          üö™ Log Out
        </button>
      </div>

      {/* Right Content Area */}
      <div style={{ flex: 1, padding: "30px", overflowY: "auto" }}>
        {renderTabContent()}
      </div>
    </div>
  );
};

export default Dashboard;
